  #random-index
    width: 100%;
    padding: 5px;
  #random-index .index-nav-link
    margin: 5px;
  .social-icon
    margin: 0px;
  #bottom-outer, #random-index
    opacity: mobileOpacityValue;
  #menu-outer
    opacity: mobileOpacityValue;

@media (max-height: 480px)
