    position: static;
    top: 0;
    left: 0;
    margin-left: 0px;
    margin-top: 95px;
    width: 100%;
  #random-index
    width: 100%;
    padding: 5px;
  #random-index .index-nav-link
    li
      display: block;
      padding: 0px 0px;
@media (max-height: 320px)
  .random-center
    margin-top: 75px;

