}

// menu start
#menu-outer
  margin: 0px;
  padding: 0px;
  position: fixed;
  z-index: 1;
  transition-property: all;
  transition-duration: .5s;
  transition-timing-function: cubic-bezier(0, 1, 0.5, 1);

.slide-up
  top: -50px;

.slide-down
  top: 0;

#menu-inner
  line-height: 50px;
  height: 49px;
  border-bottom: 1px solid #ddd;
  // height: 20px;
  padding: 0px marginBorder 0 marginBorder;
  width: lgDesktopWidth - marginBorder * 2;
  @media (max-width: lgDesktopWidth)
    width: mdDesktopWidth - marginBorder * 2;
  @media (max-width: mdDesktopWidth)
    width: desktopWidth - marginBorder * 2;

#menu-inner .left
  float: left;

#menu-inner #brand
  // margin: 0px;
  float: left;
  // display: inline-block;
  vertical-align: middle;
  cursor: pointer;

#menu-inner #homelink
  line-height: 32px;
  display: inline-block;

#menu-inner #brand a
  top: 0;
  color: #000;

#menu-inner #brand a:hover
  background-color: #fff;
  text-decoration: none;

#menu-list
  line-style: none;
  display: inline-block;
  float: right;
  padding-left: 0px;

#menu-list ul
  margin: 0px;

#menu-list ul li
  padding: 5px;
  margin: 5px;
  text-align: center;
  display: inline;

#menu-list ul li.active
  font-weight: 900;
#menu-list ul li.active a
  color: #000;

#menu-list ul li:hover
  font-weight: 900;

#menu-list ul li a
  text-decoration: none;

#menu-outer #avatar
  border-radius: 50%;
  border: 2px wihte solid;
  box-shadow: 0 0px 3px 1px rgba(0,0,0,0.3);
  vertical-align: middle;
  width: 32px;
  height: 32px;

// menu end

// bottom start
#bottom-outer
  color: #888;
  text-align: center;
  padding: marginBorder;
  padding-top: 40px;
  background-color: #fff;
  font-size: 14px;
  @media (max-width: lgDesktopWidth)
    font-size: 12px;
// bottom end

// content start
#container
  margin-top: 50px;

#content-outer
  padding: marginBorder;

// content end

