.side-navigate
  position: fixed;
  top: 60%;
  width: 100%;
  z-index: 2;
  pointer-events: none;

.side-navigate .item
  display: table-cell;

.side-navigate .item-icon:hover
  // border: 5px #52B3D9 solid;
  opacity: 1;
  background-size: 40px 40px;

.side-navigate .item-icon
  pointer-events: auto;
  opacity: 0.55;
  padding: 20px;
  border-radius: 50%;
  border: 1px #52B3D9 solid;
  width: 30px;
  height: 30px;
  background-repeat: no-repeat;
  background-position: center;
  background-size: 30px 30px;

// .side-navigate .item-icon:hover
  // display: none;
.side-navigate a:hover + div
  display: block;
.side-navigate a:hover
  display: block;

.side-navigate .prev .item-icon
  background-image: url('prev.png');
  float: left;
.side-navigate .prev
  display: inline-block;
  left: 0;

.side-navigate .item-title
  display: none;
  padding: 10px;
  max-width: 300px;
  vertical-align: center;
  background-color: #52B3D9;
  height: 52px;

.side-navigate .next .item-icon
  background-image: url('next.png')
  float: right;
.side-navigate .prev .item-title
  margin-left: 70px;
.side-navigate .next .item-title
  margin-right: 70px;

.side-navigate .next
  // text-align: right;
  display: inline-block;
  float: right;
  right: 0;

.fancybox-thumb:hover
  text-decoration: none;
.fancybox-thumb img
  max-width: 120px;
  max-height: 120px;
  padding: 3px;
  border-radius: 5%;
  border: 1px solid #DDD;

.caption
  display: block;
  text-align: center;
  font-size: 14px;
  color: #999;

.random-toc-area
  position: fixed;
  width: 200px;
  right: 0px;
  top: 0px;
  display: block;
  @media (max-width: lgDesktopWidth + 400px)
    display: none;

.random-toc
  // top: 30px;
  background: #444;
  color: #bbb;
  height: 500px;
  margin-top: 0px;
  font-size: 12px;
  height: 100%;
  overflow-y: auto;
.random-toc ol
  padding-left: 15px;
.random-toc h2
  text-align: center;
.random-toc a
  color: #bbb;
  display: block;
// .random-toc li:hover
.toc-text
  display: block;
.toc-text:hover
  background-color: #666;
.btn-hide-toc
  // top: 0px;
  border: none;
  z-index: 1;
  display: block;
  width: 100%;
  background-color: #666;
  height: 30px;
  color: #bbb;
.btn-hide-toc:hover
  background-color: #888;

// code style
figure.highlight
  margin: 0px -10px 0px -10px;

  td.gutter
    padding-right: 20px;

  .line
    height: codeLineHeight;

code
  font-family: codeFontFamily;
  background-color: #ddd;
  font-style: italic;
  padding-left: 12px;
  padding-right: 12px;

table .line
  font-family: codeFontFamily;
  height: codeLineHeight;

blockquote
  background-color: #EEE;
  padding: 2px 10px;
  border-left: 2px solid #999;

// side-navigate, hide while max-width < desktopWidth + 300px
@media (max-width: desktopWidth + 300px)
  .side-navigate
    display: none;
  .jiathis_style
    display: none;
    visibility: hidden;

// mobile
@media (max-width: desktopWidth)
  #outer-container,.random-center,#menu-outer
    width: 100%;
  #outer-container
    left: 0%;
    margin-left: 0px;
  #menu-inner
    width: 95%;
    padding: 0 2%;
  .post-title-item
    margin-left: 3px;
  #show-menu
    display: inline-block;
  article img
    width: 95%
  #menu-list
    display: none;
    position: absolute;
    margin-top: 50px;
    background-color: #FFF;
    opacity: opacityValue;
    width: 100%;
  #menu-list ul
    padding-left: 0px;
  #menu-list ul li
    text-align: center;
    display: block;
  .highlight
    margin-left: 0px;
    margin-right: 0px;
  // #user-card
    // width: 200px;
@media (max-width: mobileMaxWidth)
  .random-center
    position: static;
    left: 0;
    margin-left: 0px;
    width: 100%;
  #random-index
    width: 100%;
    padding: 5px;
  #random-index .index-nav-link
    margin: 5px;
  .social-icon
    margin: 0px;
  #bottom-outer, #random-index
    opacity: mobileOpacityValue;
  #menu-outer
    opacity: mobileOpacityValue;

@media (max-height: 480px)
  .random-center
    position: static;
    top: 0;
    left: 0;
    margin-left: 0px;
    margin-top: 95px;
    width: 100%;
  #random-index
    width: 100%;
    padding: 5px;
  #random-index .index-nav-link
    li
      display: block;
      padding: 0px 0px;
@media (max-height: 320px)
  .random-center
    margin-top: 75px;

