  max-width: 120px;
  max-height: 120px;
  padding: 3px;
  border-radius: 5%;
  border: 1px solid #ddd;
}
.caption {
  display: block;
  text-align: center;
  font-size: 14px;
  color: #999;
}
.random-toc-area {
  position: fixed;
  width: 200px;
  right: 0px;
  top: 0px;
  display: block;
}
