  color: #bbb;
  height: 500px;
  margin-top: 0px;
  font-size: 12px;
  height: 100%;
  overflow-y: auto;
}
.random-toc ol {
  padding-left: 15px;
}
.random-toc h2 {
  text-align: center;
}
.random-toc a {
  color: #bbb;
  display: block;
}
.toc-text {
  display: block;
}
.toc-text:hover {
  background-color: #666;
}
.btn-hide-toc {
  border: none;
  z-index: 1;
  display: block;
  width: 100%;
  background-color: #666;
  height: 30px;
  color: #bbb;
}
.btn-hide-toc:hover {
  background-color: #888;
}
figure.highlight {
  margin: 0px -10px 0px -10px;
}
figure.highlight td.gutter {
  padding-right: 20px;
}
figure.highlight .line {
  height: 22px;
}
code {
  font-family: SFMono-Regular, Consolas, Liberation Mono, Menlo, monospace;
  background-color: #ddd;
  font-style: italic;
  padding-left: 12px;
  padding-right: 12px;
}
table .line {
  font-family: SFMono-Regular, Consolas, Liberation Mono, Menlo, monospace;
  height: 22px;
}
blockquote {
  background-color: #eee;
  padding: 2px 10px;
  border-left: 2px solid #999;
