@media (max-width: 1060px) {
  .side-navigate {
    display: none;
  }
  .jiathis_style {
    display: none;
    visibility: hidden;
  }
}
@media (max-width: 760px) {
  #outer-container,
  .random-center,
  #menu-outer {
    width: 100%;
  }
  #outer-container {
    left: 0%;
    margin-left: 0px;
  }
  #menu-inner {
    width: 95%;
    padding: 0 2%;
  }
  .post-title-item {
    margin-left: 3px;
